import { BrowserRouter, Routes, Route } from 'react-router-dom';
import React, {useEffect, useState} from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
// import { Carousel } from 'react-responsive-carousel';
import { Carousel } from 'react-bootstrap';
import './App.css';
import { Header } from './Header';

function App() {
  const [hello, setHello] = useState('')

  useEffect(() => {
      axios.get('/api/hello')
      .then(response => setHello(response.data))
      .catch(error => console.log(error))
  }, []);

  return (
    <div className="App">
      <div> 
          {hello}
      </div>
      {/* 데이터 확인 테스트용 */}
      <Header/>
      <Announce/> 
      <SideBar/>
      <Category/>
      <StoreListItem/>
      <Footer/>
    </div>
  );
}



function Footer() {
  return (
    <div className='footer-box'>
      <img src='../LogoPage.png' />
    </div>
  )
}

function SideBar() {
  return (
    <div className='side-bar-box'>
      <a href='/'>등록</a>
      <a href='/'>오늘의 <br/>방사능</a>
      <a href='/'>챗봇</a>
    </div>
  )
}

function Announce() {
  const carousel = {
    height: '400px',
    width: '60%',
    margin: '0 auto',
    borderRadius: '30px',
    overflow: 'hidden'
  }
  return (           
    <div style={carousel}>
    <Carousel >
      {/* 슬라이드 이미지 */}
      <Carousel.Item id='carousel'>
        <img
          src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS8_I0twC6CuVzdVxXirXhxPbCOZTrwBFFqjmeX4pTw&s"
          alt="슬라이드 1"
        />
        <Carousel.Caption>
          <h3>꽃소녀</h3>
          <p>김철수 作</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item id='carousel'>
        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS8_I0twC6CuVzdVxXirXhxPbCOZTrwBFFqjmeX4pTw&s" alt="슬라이드 2" />
        <Carousel.Caption>
          <h3>형형색색</h3>
          <p>이숙자 作</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item id='carousel'>
        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS8_I0twC6CuVzdVxXirXhxPbCOZTrwBFFqjmeX4pTw&s" alt="슬라이드 3" />
        <Carousel.Caption>
          <h3>분홍</h3>
          <p>Robert Park 作</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item id='carousel'>
        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS8_I0twC6CuVzdVxXirXhxPbCOZTrwBFFqjmeX4pTw&s" alt="슬라이드 4" />
        <Carousel.Caption>
          <h3>두꺼운 터치</h3>
          <p>Thomas Lee 作</p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>
    </div>
  );
}



function Category() {

  const [selectedOption, setSelectedOption] = useState('');

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);}

  return (
    <>
      <select value={selectedOption} onChange={handleOptionChange}>
        <option value="option1">옵션 1</option>
        <option value="option2">옵션 2</option>
        <option value="option3">옵션 3</option>
      </select>

      <p>{selectedOption}</p>
    </>
  );
}

function StoreListItem() {
  return (
    <div className='store-list-item-box'>
      <img src='/boss.png' className='store-list-item-boss'/>
      <div className='store-list-item-detail-top'>
        <div className='store-list-item-detail-header'>
          <strong className='store-list-item-detail-name'>해피수산</strong>
          <span className='store-list-item-detail-market'>노량진</span>
        </div>
        <p className='store-list-item-detail-introduce'>신선신선</p>
      </div>
      <div className='store-list-item-detail-bottom'>
        <p><img src='' className='store-list-item-box-review'/>5.0 (1024)</p>
        <p className='store-list-item-box-state'>영업중</p>
        <a href='/' className='store-list-item-box-more'>더보기&gt;</a>
      </div>

    </div>
  );
}






export default App;

{/*
<header className="App-header">
<img src={logo} className="App-logo" alt="logo" />
<p>
  여기는 킹종원이 점령했다.
</p>
<a
  className="App-link"
  href="https://reactjs.org"
  target="_blank"
  rel="noopener noreferrer"
>
  Learn React
</a>
</header>
*/}
